void tty_start() {
	// load tty process
	code_bytes_t* code = code_get("/bin/ottos_tty");
	if(code == NULL) {
		kernel_panic("Cannot start tty");
		return;
	}
	process_create(1, code);

	// TODO (thomas.bargetz@gmail.com) implement code_free function
	free(code->byte_0);
	free(code->byte_1);
	free(code->byte_2);
	free(code->byte_3);
	free(code);
}

void startup() {
	irq_disable();

	irq_init();
	timer_init();
	process_table_init();
	devices_init();
	mmchs_init();
	fs_init();
	mmu_init();

	tty_start();

	irq_register_context_switch();
	irq_enable();
	kernel_to_user_mode();
}

