  irq_disable();
  mmu_init();
  //devices_init();
  //process_table_init();
  irq_init();
  timer_init();

  code_bytes.byte_0 = byte0;
  code_bytes.byte_1 = byte1;
  code_bytes.byte_2 = byte2;
  code_bytes.byte_3 = byte3;

  process_create(1, &code_bytes);
//
//  code_bytes.byte_0 = byte0_;
